    const[username, setUserName] = useState('');
    const[pass,setPass] = useState('');

    const log=(e) =>{
        e.preventDefault();
        if(validate()){
            fetch("http://localhost:3000/users"+username).then((res) => {
                return res.json();

            })
            .then((resp) => {
                console.log(resp)
            })
            .catch((err)=> {
                toast
            })
        }
    }











    return (



        <div className="container">

            <div>
                <label className="account">ID ACCOUNT </label>
                <input type="text" name="account" className="info"/>

            </div>

            <div>
                <label className="account">PASSWORD </label>
                <input type="password" name="pass" className="info"/>

            </div>

            <div className="btn1">

                <button className="submit" onClick={log}>Log In</button>

                <button className="cancel" navigate="/" value="cancel">Cancel</button>

            </div>

            <div>
                <a className = "forget" href="">Forgot Password ???</a>

            </div>  




        // </div>




    )
}

export default Login
