import Data from './videos.json'
import React from 'react'
import { renderMatches } from 'react-router-dom'

class Pilote extends React.Component{

    videoClick = (event) => {

        const vidEl = document.getElementById(event.currentTarget.id)
        if (vidEl.requestFullscreen){
            vidEl.requestFullscreen()
        }


     }

render()
{

return (


    <div>

    { Data.map( record => {

        // let a = require("" + record.src)

            return (

                <div className="box" key="record.id">
                    <div className='account'>
                    { record.title }

                    </div>

                    <video className="v2" width="100" height="80" controls autoPlay={true} loop={true} id={record.id} 
                                                                        onClick={this.videoClick}>

                        <source src = {require(""+record.src)} type="video/mp4" />

                    </video>

                </div>


            )
        })
    }

    {/* <button onClick={view}>OK </button> */}



</div>

)

}
}

export default Pilote
