import {useState} from 'react'
import {toast } from 'react-toastify'
import { useNavigate } from 'react-router-dom';


export const Login=()=> {
    let navigate = useNavigate();

    const[username, setUserName] = useState('');
    const[pass,setPass] = useState('');

    const log =(e) =>{

        e.preventDefault();
        console.log('hello')

        if(validate()){
            fetch("http://localhost:3000/db.json").then((res) => {
                return res.json();

            })
            .then((resp) => {
                console.log(resp)
               if (Object.keys(resp).length ===0){
                   toast.error('Entrez votre username');
               }else{
                   console.log('line27')
                   console.log(resp)
                   if(resp.fil === pass){
                       navigate('/pilote')

                   }else{
                       toast.error('il y a error')
                   }
               }   
            })
            .catch((err)=> {
                toast.error('Error')
            })
        }
    }



    const validate = () => {
        let result = true;
        if (username === '' || username === null) {
            result = false;
            toast.warning('Please Enter Username');
        }
        if (pass === '' || pass === null) {
            result = false;
            toast.warning('Please Enter Password');
        }
        return result;
    }




    return (



        <div className="container">
            <form onSubmit={log}> 

            <div>
                <label className="account">ID ACCOUNT </label>
                <input type="text" name="account" value={username} onChange={e => setUserName(e.target.value)} className="info"/>

            </div>

            <div>
                <label className="account">PASSWORD </label>
                <input type="password" name="pass" value={pass} onChange={e => setPass(e.target.value)} className="info"/>

            </div>

            <div className="btn1">

                <button className="submit" onClick={log}>Log In</button>

                <button className="cancel" navigate="/" value="cancel">Cancel</button>

            </div>

            <div>
                <a className = "forget" href="">Forgot Password ???</a>

            </div>  


            </form>

         </div>




    )
}

export default Login
